sourcesContent:
import carouselModel from "../models/carousel.model";
import multer from "multer";
import path from "path";
import fs from 'fs';

const storage = multer.diskStorage({
    destination : function(req,file,cb) {
       if(fs.existsSync('./uploads/carousel')){
        cb(null , './uploads/carousel')
       }else{
        fs.mkdirSync('./uploads/carousel')
        cb(null , './uploads/carousel')
       }
    },

    filename: function (req,file,cb){
        let orName = file.originalname;
        let ext = path.extname(orName);
        let basename = path.parse(orName).name;
        let filename = basename + '-' + Date.now() + ext
        cb(null , filename)
    }
})

const upload = multer({storage:storage})

export const getCarousels = async(req,res)=>{
    try {
        const carousels = await carouselModel.find();
        if(carousels){
            return res.status(200).json({
                data:carousels,
                message:'fetched'
            })
        }
        return res.status(400).json({
            message:'Bad Request'
        })
        
    } catch (error) {
        return res.status(500).json({
            message:error.message
        })
    }
}


export const addCarousel = (req,res)=>{
    try {

        const carouselData = upload.single("image");
        carouselData(req,res,function(err){
            if(err) return res.status(400).json({
                message:err.message
            })

            const {name, short_description} = req.body;

            let img = null;
            if(req.file){
                img = req.file.filename;

            }

            const saveData = new carouselModel({
                name:name,
                short_description: short_description,
                image: img
            })

            saveData.save();

            if(saveData){
                return res.status(201).json({
                    data:saveData,
                    message:"Created"
                })
            }
            return res.status(400).json({
                message:"Bad Request"
            })
        })

        
    } catch (error) {
        return res.status(500).json({
            message:error.message
        })
        
    }

}

