import express from "express";
import { addlatestCompaign, deleteCompaign, getlatestCompaign, getlatestCompaigns, updateCompaign } from "../../controllers/home/latestcompaign.controller";

const router = express.Router();
router.get("/get-latestcompaigns", getlatestCompaigns)
router.post("/add-compaign", addlatestCompaign)
router.get("/get-compaign/:compaign_id",getlatestCompaign)
router.put('/update-compaign/:compaign_id',updateCompaign)
router.delete('/delete-compaign/:compaign_id', deleteCompaign)


export default router

