import mongoose from "mongoose";

const Schema = mongoose.Schema;
const LatestCompaign = new Schema({
    title:{
        type:String,
        required:true,
    },

    description:{
        type:String,
        required:true,
    }, 
    
    image:{
        type:String,
        default:null
    },

    status:{
        type:Number,
        default:1,
    },

    createdAt:{
        type:Date,
        default:Date.now(),
    }
})

export default mongoose.model('latestCompaign',LatestCompaign)

