sourcesContent:
import latestcompaignModel from "../models/latestcompaign.model";
import multer from "multer";
import fs from "fs";
import path from "path";

const storage = multer.diskStorage({
  destination: function (req, file, cb) {
    if (fs.existsSync("./uploads/latestCompaign")) {
      cb(null, "./uploads/latestCompaign");
    } else {
      fs.mkdirSync("./uploads/latestCompaign");
      cb(null, "./uploads/latestCompaign");
    }
  },
  filename: function (req, file, cb) {
    let orName = file.originalname;
    let ext = path.extname(orName);
    let basename = path.parse(orName).name;
    let filename = basename + "-" + Date.now() + ext;
    cb(null, filename);
  },
});
const upload = multer({ storage: storage });

export const getlatestCompaigns = async (req, res) => {
  try {
    const latestCompaigns = await latestcompaignModel.find();
    if (latestCompaigns) {
      return res.status(200).json({
        data: latestCompaigns,
        message: "fetched",
        filepath: "http://localhost:8008/uploads/latestCompaign",
      });
    }

    return res.status(400).json({
      message: "Bad Request",
    });
  } catch (error) {
    return res.status(500).json({
      message: error.message,
    });
  }
};

export const addlatestCompaign = (req, res) => {
  try {
    const addImage = upload.single("avatar");
    addImage(req, res, function (err) {
      if (err) return res.status(400).json({ message: err.message });
      const { title, description, days, raised, goal, donor} = req.body;
      let filename = null;
      if (req.file) {
        filename = req.file.filename;
      }

      const savelatestCompaign = new latestcompaignModel({
        title: title,
        description: description,
        days: days,
        raised:raised,
        goal:goal,
        donor:donor,
        image: filename,
      });
      savelatestCompaign.save();

      if (savelatestCompaign) {
        return res.status(201).json({
          data: savelatestCompaign,
          message: "created",
        });
      }

      return res.status(400).json({
        message: "Bad Request",
      });
    });
  } catch (error) {
    return res.status(500).json({
      message: error.message,
    });
  }
};

export const getlatestCompaign = async (req, res) => {
  try {
    const compaignID = req.params.compaign_id;
    const compaign = await latestcompaignModel.findOne({ _id: compaignID });
    if (compaign) {
      return res.status(200).json({
        data: compaign,
        message: "fetched",
        filepath: "http://localhost:8008/uploads/latestCompaign",
      });
    }

    return res.status(400).json({
      message: "Bad Request",
    });
  } catch (error) {
    return res.status(500).json({
      message: error.message,
    });
  }
};

export const updateCompaign = async (req, res) => {
  try {
    const updateImage = upload.single("avatar");
    updateImage(req, res, async function (err) {
      if (err) return res.status(400).json({ message: err.message });

      const compaignID = req.params.compaign_id;
      const existUpdate = await latestcompaignModel.findOne({_id: compaignID})
      const { title, description, days, raised, goal, donor } = req.body;

      let filename = existUpdate.image;
      if (req.file) {
        filename = req.file.filename;

        if(fs.existsSync("./uploads/latestCompaign/" + existUpdate.image)){
          fs.unlinkSync('./uploads/latestCompaign/' + existUpdate.image)
        }
      }
      
      const update_compaign = await latestcompaignModel.updateOne(
        { _id: compaignID },
        {
          $set: {
            title: title,
            description: description,
            days: days,
            raised:raised,
            goal:goal,
            donor:donor,
            image: filename,
          },
        }
      );

      if (update_compaign) {
        return res.status(200).json({
          message: "Updated",
        });
      }
      return res.status(400).json({
        message: "Bad Request",
      });
    });
  } catch (error) {
    return res.status(500).json({
      message: error.message,
    });
  }
};


export const deleteCompaign = async(req,res)=>{
  try {
    const compaignID = req.params.compaign_id;
    const existDelete = await latestcompaignModel.findOne({_id:compaignID})
    const delete_compaign = await latestcompaignModel.deleteOne({_id:compaignID});
    if(delete_compaign){
      if(fs.existsSync("./uploads/latestCompaign/" + existUpdate.image)){
        fs.unlinkSync('./uploads/latestCompaign/' + existUpdate.image)
      }
      return res.status(200).json({
        message:"Deleted"
      })
    }

    return res.status(400).json({
      message:"Bad Request"
    })
    
  } catch (error) {
    return res.status(500).json({
      message:error.message
    })
    
  }
}

